	function checkeligibleornot(){
		var cid = $('#course').val();
		var uid = $('#user').val();

			$.ajax({
				url: './validateeligible.php',
				type: 'post',
				data: {cid:cid,uid:uid},
				success:function(response){                

					if(response >= 2){
						$('#txtmsg').show();
						const subhs = document.getElementById('submit');
						subhs.toggleAttribute("disabled", true);

					} else {
						$('#txtmsg').hide();
						const subhs = document.getElementById('submit');
						subhs.toggleAttribute("disabled", false);

					}
			 	}
			});

	}
















